    /**
     * 动态规划 以时间换空间
     */
    public int uniquePathsWithObstacles(int[][] obstacleGrid) {
        int row = obstacleGrid.length;
        int col = obstacleGrid[0].length;
        int[][] steps = new int[row][col];
        for (int j = col - 1; j >= 0; j--) {
            for (int i = row - 1; i >= 0; i--) {
                if (obstacleGrid[i][j] == 1) {
                    steps[i][j] = 0;
                } else if (i == row - 1 && j == col - 1) {
                    steps[i][j] = 1;
                } else {
                    if (i < row - 1) {
                        steps[i][j] += steps[i + 1][j];
                    }
                    if (j < col - 1) {
                        steps[i][j] += steps[i][j + 1];
                    }
                }
            }
        }
        return steps[0][0];
    }

    /**
     * 递归 计算超时
     */
    public int uniquePathsWithObstaclesRecursion(int[][] obstacleGrid) {
        return uniquePathsWithObstaclesRecursion(obstacleGrid, obstacleGrid.length - 1, obstacleGrid[0].length - 1);
    }

    private int uniquePathsWithObstaclesRecursion(int[][] obstacleGrid, int m, int n) {
        if (m < 0 || n < 0 || obstacleGrid[m][n] == 1) {
            return 0;
        }
        if (m == 0 && n == 0) {
            return 1;
        }
        return uniquePathsWithObstaclesRecursion(obstacleGrid, m - 1, n) + uniquePathsWithObstaclesRecursion(obstacleGrid, m, n - 1);
    }
